        add("item.confluence.medicated_bandage.info3", "It is one of the ingredients required to make the Ankh Charm, which is further required to make the Ankh Shield.");
        add("item.confluence.megaphone.info", "The Megaphone is an immunity accessory that grants the player immunity to the Silenced debuff.");
        add("item.confluence.megaphone.info2", "It has a 1/100 (1%) / 1/50 (2%) chance to drop from Pixies, Green Jellyfish, Dark Mummies, and Blood Mummies.");
        add("item.confluence.megaphone.info3", "It is one of the ingredients required to make the Countercurse Mantra, which is a crafting material required to make the Ankh Charm, further required to make the Ankh Shield.");
        add("item.confluence.molten_skull_rose.info", "The Molten Skull Rose is that represents the final upgrade of the Obsidian Skull.");
        add("item.confluence.molten_skull_rose.info2", "It grants 7 seconds of immunity to lava, immunity from fire blocks, and reduces damage taken from lava.");
        add("item.confluence.molten_skull_rose.info3", "When worn, the item can be seen on the players head.");
        add("item.confluence.molten_skull_rose.info4", "This item can also be crafted in three different ways.");
        add("item.confluence.moon_stone.info", "The Moon Stone is a Hardmode accessory which has a 1/35 (2.86%) / 69/1225 (5.63%) chance to drop from Vampires during the Solar Eclipse event.");
        add("item.confluence.moon_stone.info2", "It grants the same stat bonuses as the Sun Stone, but these bonuses will only activate during the night or during a Solar Eclipse.");
        add("item.confluence.moon_stone.info3", "The stat bonuses are:");
        add("item.confluence.moon_stone.info4", "+10% melee speed");
        add("item.confluence.moon_stone.info5", "+10% damage (all types)");
        add("item.confluence.moon_stone.info6", "+2% critical strike chance");
        add("item.confluence.moon_stone.info7", "+1 HP/s health regeneration");
        add("item.confluence.moon_stone.info8", "+4 defense");
        add("item.confluence.moon_stone.info9", "+15% mining speed");
        add("item.confluence.moon_stone.info10", "+0.5 minion knockback");
